
"use strict";function generateStartPage(){const n=getAssets("startPage");return`<div class="row">\n        <div class="col-6">\n            <img src="${n.startImg}" alt="${n.startImg}">\n        </div>\n        <div class="col-6">\n            <p>${n.startMsg}</p>\n            <button type="button" class="btnStartQuiz">Begin Quiz</button>\n        </div>\n    </div>`}function generateAnswerItem(n,e){return`<label for="${e}">\n        <input type="radio" id="${e}" name="questions" value="${n}" required>\n        ${n}\n    </label>`}function generateAnswerList(n){return n.answers.map((n,e)=>generateAnswerItem(n,e)).join("")}function generateQuestion(n){const e=generateAnswerList(n);return`<form class="quiz-app">\n        <div class="row question">\n            <div class="col-12">\n                <fieldset>\n                    <legend>${n.question}</legend>\n                    ${e}\n                </fieldset>\n            </div>\n        </div>\n        <div class="row">\n            <div class="col-12">\n                <button type="submit" class="btnSubmitAnswer">Submit</button>\n            </div>\n        </div>\n    </form>`}function generateQuizStatus(){return`<div class="col-12">\n        <div class="score">\n            <span><strong>Question</strong>: ${STORE.currentQuestion}/${QUESTIONS.length}</span>\n            <span><strong>Score</strong>: ${STORE.userScore}/${QUESTIONS.length}</span>\n        </div>\n    </div>`}function generateQuestionResult(){const n=getAssets(STORE.currentAnswer),e=getCorrectAnswer();return`<div class="row">\n        <div class="col-3 empty">&nbsp;</div>\n        <div class="col-6">\n            <h2>${n.resultMsg}</h2>\n            <img src="${n.resultImg}" alt="${n.resultImgAlt}">\n        </div>\n        <div class="col-3 empty">&nbsp;</div>\n    </div>\n\n    ${"wrongAnswer"===STORE.currentAnswer?`<div class="row correctAnswer">\n        <div class="col-12 ">\n            <p>The correct answer was obviously '<strong>${e}</strong>'!</p>\n        </div>\n    </div>`:""}\n\n    <div class="row">\n        <div class="col-12">\n            <button type="button" class="btnNextQuestion">NEXT QUESTION</button>\n        </div>\n    </div>`}function generateFinalResult(){const n=getAssets("finalResult");return`<div class="row">\n        <div class="col-4 empty">&nbsp;</div>\n        <div class="col-4 finalResult">\n            <h2>FINAL SCORE</h2>\n            <p><strong>${STORE.userScore}</strong> out of <strong>${QUESTIONS.length}</strong><p>\n        </div>\n        <div class="col-4 empty">&nbsp;</div>\n    </div>\n    <div class="row">\n        <div class="col-2 empty">&nbsp;</div>\n        <div class="col-8">\n            <p class="finalResultMsg">${STORE.userScore>QUESTIONS.length/2?`${n.goodResultMsg}`:`${n.badResultMsg}`}</p>\n        </div>\n        <div class="col-2 empty">&nbsp;</div>\n    </div>\n    <div class="row">\n        <div class="col-3 empty">&nbsp;</div>\n        <div class="col-6">\n        ${STORE.userScore>QUESTIONS.length/2?`<img src="${n.goodResultImg}" alt="${n.goodResultImgAlt}">`:`<img src="${n.badResultImg}" alt="${n.badResultImgAlt}">`}\n        </div>\n        <div class="col-3 empty">&nbsp;</div>\n    </div>\n    <div class="row">\n        <div class="col-12">\n            <button type="button" class="btnRestartQuiz">PLAY AGAIN</button>\n        </div>\n    </div>`}function getAssets(n){return ASSETS.find(function(e){return e.id===n})}function getCurrentQuestion(){return QUESTIONS.find(function(n){return n.id===STORE.currentQuestion})}function getCurrentQuestionIndex(){return QUESTIONS.findIndex(function(n){return n.id===STORE.currentQuestion})}function getCorrectAnswer(){const n=getCurrentQuestionIndex(),e=QUESTIONS[n].correctAnswer;return QUESTIONS[n].answers[e]}function updateUserScore(){STORE.userScore++}function checkAnswer(){$("input:checked").val()===getCorrectAnswer()?(STORE.currentAnswer="correctAnswer",updateUserScore()):STORE.currentAnswer="wrongAnswer"}function advanceToNextQuestion(){STORE.currentQuestion++}function clearAnswer(){STORE.currentAnswer=null}function restartQuiz(){STORE.view="start",STORE.currentQuestion=1,STORE.userScore=0}function renderStartQuiz(){$(".intro").html(generateStartPage())}function renderQuestionText(){const n=getCurrentQuestion();$(".quiz").html(generateQuestion(n))}function renderQuizStatusBar(){$(".quizStatus").html(generateQuizStatus())}function renderQuestionResult(){$(".result").html(generateQuestionResult())}function renderFinalResult(){$(".result").html(generateFinalResult())}function render(){"start"===STORE.view?(renderStartQuiz(),$(".intro").show(),$(".quiz").hide(),$(".result").hide(),$(".quizStatus").hide()):"quiz"===STORE.view?(renderQuestionText(),renderQuizStatusBar(),$(".intro").hide(),$(".quiz").show(),$(".result").hide(),$(".quizStatus").show()):"questionResult"===STORE.view?(renderQuestionResult(),renderQuizStatusBar(),$(".intro").hide(),$(".quiz").hide(),$(".result").show(),$(".quizStatus").show()):"finalResult"===STORE.view&&(renderFinalResult(),$(".intro").hide(),$(".quiz").hide(),$(".result").show(),$(".quizStatus").hide())}function handleQuizStart(){$(".intro").on("click",".btnStartQuiz",function(n){STORE.view="quiz",render()})}function handleAnswerSubmitted(){$(".quiz").on("submit",function(n){n.preventDefault(),checkAnswer(),STORE.view="questionResult",render()})}function handleNextQuestion(){$(".result").on("click",".btnNextQuestion",function(n){clearAnswer(),STORE.currentQuestion<QUESTIONS.length?(advanceToNextQuestion(),STORE.view="quiz"):STORE.view="finalResult",render()})}function handleRestartQuiz(){$(".result").on("click",".btnRestartQuiz",function(n){restartQuiz(),render()})}function initializePage(){handleQuizStart(),handleAnswerSubmitted(),handleNextQuestion(),handleRestartQuiz(),render()}$(initializePage());